import React from 'react';
import './FeaturedItems.css';
import heroBackground from '../../images/hero-background.jpg'; // Using as placeholder

const FeaturedItems = () => {
  const imageStyle = {
    backgroundImage: `url(${heroBackground})`
  };

  return (
    <section className="featured-items-section">
      <div className="uk-container">
        <div className="uk-text-center section-header">
          <h2>CELEBRATIONS</h2>
          <hr className="divider" />
        </div>
        
        <div className="uk-text-center section-subtitle">
          <h3>Celebrate with us.</h3>
        </div>
        
        <div className="section-description uk-text-center">
          <p>Join us to celebrate the milestones in your life. Whether that be a wedding reception, birthday, graduation or perhaps securing that new job, we are the perfect location to celebrate.</p>
          
          <p>Kaboul Gourmet can be the perfect backdrop for an intimate wedding reception, from securing one of our private dining rooms or exclusively hiring our restaurants for your big day. Small or larger private tables can be reserved to make your special occasion.</p>
          
          <p>We welcome special requests, from a bouquet of flowers to a small celebration cake please talk to our team and they will try their best to meet your needs and wants of your special day. Our team are on hand for all those pre-ordered bottles of champagne, a signed menu or even a cook-book amongst a handful of options to mark the special occasion</p>
        </div>
        
        <div className="uk-text-center uk-margin-large-top cta-container">
          <a href="#book" className="featured-button primary">MAKE A BOOKING</a>
          <a href="#team" className="featured-button secondary">TALK TO OUR TEAM</a>
          <a href="#private" className="featured-button secondary">PRIVATE DINING ENQUIRY</a>
        </div>
      </div>
    </section>
  );
};

export default FeaturedItems;
