import React from 'react';
import './Intro.css';
import introImage from '../../images/hero-background.jpg'; // Using same image as placeholder

const Intro = () => {
  const imageStyle = {
    backgroundImage: `url(${introImage})`
  };

  return (
    <section className="intro-section">
      <div className="container">
        <div className="uk-grid uk-grid-large intro-grid">
          <div className="uk-width-1-2@m intro-content">
            <div className="heading-container">
              <h2>AUTHENTIC AFGHAN CUISINE</h2>
              <hr className="divider" />
            </div>
            <p className="elegant-description">
              Kaboul Gourmet brings the finest authentic Afghan cuisine to Chelsea. Our menu features traditional dishes prepared with premium ingredients and expert techniques passed down through generations.
            </p>
            
            <div className="uk-flex uk-flex-middle uk-margin-medium-top cta-group">
              <a href="#book" className="intro-cta primary">BOOK A TABLE</a>
              <a href="#menus" className="intro-cta secondary">EXPLORE OUR MENUS</a>
            </div>
          </div>
          
          <div className="uk-width-1-2@m intro-image-container">
            <div className="intro-image" style={imageStyle}></div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Intro;
