import React from 'react';
import './OurStory.css';
import heroBackground from '../../images/hero-background.jpg'; // Using as placeholder

const OurStory = () => {
  const imageStyle = {
    backgroundImage: `url(${heroBackground})`
  };

  return (
    <section className="our-story-section" id="our-story">
      <div className="uk-container">
        <div className="uk-grid uk-grid-large story-grid">
          <div className="uk-width-1-2@m story-content">
            <div className="section-header">
              <h2>OUR STORY</h2>
              <hr className="divider" />
            </div>
            
            <div className="story-text">
              <p>Established in 2015, Kaboul Gourmet brings authentic Afghan cuisine to the heart of Chelsea, London. Founded by the Ahmadi family, who brought their culinary heritage and traditional recipes from Kabul to create an exceptional dining experience.</p>
              
              <p>Drawing on generations of Afghan culinary tradition and contemporary techniques, our kitchen celebrates the rich flavors and aromas that make Afghan cuisine truly special.</p>
              
              <p>The restaurant's interior reflects Afghanistan's cultural heritage with handcrafted d√©cor and warm ambient lighting, creating an atmosphere that complements our authentic dishes.</p>
            </div>
            
            <div className="uk-margin-medium-top">
              <a href="#chef" className="story-link">MEET OUR CHEF</a>
            </div>
          </div>
          
          <div className="uk-width-1-2@m story-image-container">
            <div className="story-image" style={imageStyle}></div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default OurStory;
