import React, { useState, useEffect, useRef } from 'react';
import './Header.css';
import { Link } from 'react-router-dom';

const Header = () => {
  // State for sticky header
  const [isSticky, setIsSticky] = useState(false);
  
  // State for active dropdowns
  const [activeDropdown, setActiveDropdown] = useState(null);
  
  // Refs for detecting clicks outside dropdowns
  const dropdownRef = useRef(null);
  
  // Restaurant locations for dropdown
  const restaurants = [
    {
      name: "Kaboul Gourmet",
      address: "Spitalfields, The City of London"
    },
    {
      name: "Kaboul Bistrot & Bar",
      address: "Spitalfields, The City of London"
    },
    {
      name: "Kaboul Green Man",
      address: "Near Chelmsford in Essex"
    }
  ];
  
  // Menu items with nested submenu items
  const menuItems = [
    {
      name: "Menus",
      link: "/menus",
      submenu: [
        { name: "Ã€ La Carte", link: "/menus/a-la-carte" },
        { name: "Set Lunch Menu", link: "/menus/set-lunch" },
        { name: "Tasting Menu", link: "/menus/tasting-menu" },
        { name: "Sunday Lunch", link: "/menus/sunday-lunch" },
        { name: "Wine List", link: "/menus/wine-list" }
      ]
    },
    {
      name: "What's On",
      link: "/whats-on",
      submenu: [
        { name: "Monthly Wine Event", link: "/whats-on/wine-event" },
        { name: "Mother's Day", link: "/whats-on/mothers-day" },
        { name: "Wine Dinner", link: "/whats-on/wine-dinner" }
      ]
    },
    {
      name: "Private Dining",
      link: "/private-dining",
      submenu: [
        { name: "The Gallery", link: "/private-dining/gallery" },
        { name: "The Arch", link: "/private-dining/arch" },
        { name: "Exclusive Hire", link: "/private-dining/exclusive-hire" }
      ]
    },
    {
      name: "About",
      link: "/about"
    },
    {
      name: "Reviews",
      link: "/reviews"
    },
    {
      name: "Contact",
      link: "/contact"
    },
    {
      name: "Newsletter Signup",
      link: "/newsletter"
    }
  ];
  
  // Add scroll event listener for sticky header
  useEffect(() => {
    const handleScroll = () => {
      setIsSticky(window.scrollY > 0);
    };
    
    window.addEventListener('scroll', handleScroll);
    
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);
  
  // Add event listener for clicks outside dropdown
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setActiveDropdown(null);
      }
    };
    
    document.addEventListener('mousedown', handleClickOutside);
    
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);
  
  // Toggle dropdown function
  const toggleDropdown = (index) => {
    setActiveDropdown(activeDropdown === index ? null : index);
  };

  // Handle navigation without React Router
  const handleNavigation = (e, path) => {
    e.preventDefault();
    console.log(`Navigation to ${path} would happen here`);
    // In a real app with React Router, you would use history.push(path)
  };
  
  return (
    <header className="site-header" ref={dropdownRef}>
      {/* Top Bar - Dark Background */}
      <div className={`top-bar ${isSticky ? 'sticky' : ''}`}>
        <div className="container">
          <div className="top-bar-inner">
            {/* Top Bar Left - Locations */}
            <div className="top-bar-left">
              <div className="locations-dropdown">
                <button className="locations-btn" onClick={() => toggleDropdown('locations')}>
                  <div className="hamburger-icon">
                    <span className="line"></span>
                    <span className="line"></span>
                    <span className="line"></span>
                  </div>
                  <span className="locations-text">Locations</span>
                </button>
                <div className={`locations-dropdown-content ${activeDropdown === 'locations' ? 'active' : ''}`}>
                  <div className="locations-header">
                    <span>Our Restaurants</span>
                  </div>
                  <ul className="locations-list">
                    {restaurants.map((restaurant, index) => (
                      <li key={index} className="location-item">
                        <span className="location-name">{restaurant.name}</span>
                        <span className="location-address">{restaurant.address}</span>
                      </li>
                    ))}
                  </ul>
                </div>
              </div>
            </div>
            
            {/* Top Bar Center - Logo */}
            <div className="top-bar-center">
              <Link to="/" className="logo-link">
                <span className="logo-text">KABOUL GOURMET</span>
              </Link>
            </div>
            
            {/* Top Bar Right - Contact & Book */}
            <div className="top-bar-right">
              <a href="tel:02072990400" className="phone-link">020 7299 0400</a>
              <Link to="/gifts" className="gift-link">Gifts</Link>
              <div className="book-dropdown">
                <button className="book-btn" onClick={() => toggleDropdown('book')}>
                  Book Now
                </button>
                <div className={`book-dropdown-content ${activeDropdown === 'book' ? 'active' : ''}`}>
                  <Link to="/book-table">Book a Table</Link>
                  <Link to="/private-dining">Private Dining</Link>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
      
      {/* Bottom Bar - White Background */}
      <div className={`bottom-bar ${isSticky ? 'sticky' : ''}`}>
        <div className="container">
          <div className="bottom-bar-inner">
            {/* Restaurant Name & Location */}
            <div className="restaurant-info">
              <h1 className="restaurant-name">Kaboul Gourmet</h1>
              <span className="restaurant-location">Spitalfields, The City of London</span>
            </div>
            
            {/* Main Navigation */}
            <nav className="main-menu">
              <ul className="menu-list">
                {menuItems.map((item, index) => (
                  <li key={index} className={item.submenu ? 'has-dropdown' : ''}>
                    <Link to={item.link}>{item.name}</Link>
                    
                    {item.submenu && (
                      <div className={`dropdown-menu ${activeDropdown === `menu-${index}` ? 'active' : ''}`}>
                        {item.submenu.map((subItem, subIndex) => (
                          <Link key={subIndex} to={subItem.link}>{subItem.name}</Link>
                        ))}
                      </div>
                    )}
                    
                    {item.submenu && (
                      <button
                        className="dropdown-toggle"
                        onClick={(e) => {
                          e.preventDefault();
                          toggleDropdown(`menu-${index}`);
                        }}
                      >
                        <span className="sr-only">Toggle {item.name} Menu</span>
                      </button>
                    )}
                  </li>
                ))}
              </ul>
            </nav>
          </div>
        </div>
      </div>
    </header>
  );
};

export default Header;
