import React, { useState } from 'react';
import './PrivateDiningCity.css';

const PrivateDiningCity = () => {
  const [activeRoomIndex, setActiveRoomIndex] = useState(0);

  const rooms = [
    {
      name: "THE GALLERY",
      location: "The City, London",
      className: "gallery"
    },
    {
      name: "THE ARCH",
      location: "The City, London",
      className: "arch"
    },
    {
      name: "EXCLUSIVE HIRE",
      location: "The City, London",
      className: "exclusive"
    }
  ];

  const handlePrevClick = () => {
    setActiveRoomIndex((prevIndex) => 
      prevIndex === 0 ? rooms.length - 1 : prevIndex - 1
    );
  };

  const handleNextClick = () => {
    setActiveRoomIndex((prevIndex) => 
      (prevIndex + 1) % rooms.length
    );
  };

  const titleStyle = {
    color: '#ffffff',
    textShadow: '0 2px 4px rgba(0, 0, 0, 0.3)'
  };

  const buttonStyle = {
    padding: '12px 40px',
    backgroundColor: '#fff',
    color: '#1a1a1a',
    border: 'none',
    whiteSpace: 'nowrap',
    fontWeight: 500,
    letterSpacing: '1px',
    fontSize: '0.9rem'
  };

  return (
    <section id="private-dining-city" className="private-dining-city-section">
      <div className="private-dining-city-container">
        <div className="section-header">
          <h2 className="section-title" style={titleStyle}>PRIVATE DINING IN THE CITY OF LONDON</h2>
          <h3 className="section-subtitle">Unforgettable Experiences at Galvin La Chapelle</h3>
          <p className="section-description">
            Unforgettable experiences for any event from intimate private dining to exclusive use 
            events upto 150 guests.
          </p>
          <div className="button-container">
            <a href="#rooms" className="discover-button" style={buttonStyle}>DISCOVER ROOMS</a>
          </div>
        </div>

        <div className="rooms-slider-container">
          <button className="slider-arrow prev" onClick={handlePrevClick} aria-label="Previous room">
            <span className="arrow-icon">&#8249;</span>
          </button>

          <div className="rooms-display">
            {rooms.map((room, index) => (
              <div 
                key={index} 
                className={`room-container ${room.className} ${index === activeRoomIndex ? 'active' : ''}`}
              >
                <div className="room-details">
                  <h4 className="room-name">{room.name}</h4>
                  <p className="room-location">{room.location}</p>
                  <div className="room-button-group">
                    <a href="#enquire" className="room-action-button">GET IN TOUCH</a>
                    <a href="#explore" className="room-action-button small">EXPLORE</a>
                  </div>
                </div>
              </div>
            ))}
          </div>

          <button className="slider-arrow next" onClick={handleNextClick} aria-label="Next room">
            <span className="arrow-icon">&#8250;</span>
          </button>
        </div>

        <div className="decorative-curve"></div>
      </div>
    </section>
  );
};

export default PrivateDiningCity;
