import React from 'react';
import './Intro.css';

const Intro = () => {
  return (
    <section className="intro-section">
      <div className="container">
        <div className="intro-grid">
          <div className="intro-content">
            <p className="elegant-description">
              Large arched windows, high stone ceilings and elegant interiors provide the perfect backdrop to Michelin starred Kaboul Gourmet's traditional yet modern Afghan menu, moments away from Chelsea High Street.
            </p>
            
            <div className="cta-group">
              <a href="#book" className="intro-cta">BOOK A TABLE</a>
              <a href="#menus" className="intro-cta">MENUS</a>
              <a href="#whats-on" className="intro-cta">WHAT'S ON</a>
            </div>
          </div>
          
          <div className="intro-image">
            {/* Placeholder for image */}
          </div>
        </div>
      </div>
    </section>
  );
};

export default Intro;
