import React from 'react';
import './HeroBanner.css';
import backgroundImage from '../../images/hero-background.jpg';

const HeroBanner = () => {
  return (
    <section className="hero-banner" style={{ backgroundImage: `url(${backgroundImage})` }}>
      <div className="hero-content">
        <h1>AUTHENTIC AFGHAN CUISINE</h1>
        <div className="hero-cta-buttons">
          <a href="#book" className="hero-btn">BOOK A TABLE</a>
          <a href="#menus" className="hero-btn">MENUS</a>
          <a href="#whats-on" className="hero-btn">WHAT'S ON</a>
        </div>
      </div>
      
      <div className="hero-testimonials">
        <div className="testimonial-item">
          <div className="quote-symbol">&ldquo;</div>
          <div className="testimonial-content">
            <p className="testimonial-text">The best of 2023's</p>
            <p className="testimonial-source">eating out (and in).</p>
            <p className="testimonial-attribution">â€” The Hot Dinners Awards</p>
          </div>
        </div>
      </div>
      
      <div className="hero-overlay"></div>
    </section>
  );
};

export default HeroBanner;
