import React, { useState, useEffect } from 'react';
import './Header.css';

const Header = () => {
  // State for sticky header
  const [isSticky, setIsSticky] = useState(false);

  // State for mobile menu
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);

  // State for dropdown
  const [activeDropdown, setActiveDropdown] = useState(null);

  // Restaurant locations
  const restaurants = [
    {
      name: "Kaboul Gourmet",
      location: "Chelsea, London",
      url: "/kaboul-gourmet"
    },
    {
      name: "Kaboul Bistrot",
      location: "Mayfair, London",
      url: "/kaboul-bistrot"
    },
    {
      name: "Kaboul Express",
      location: "Covent Garden, London",
      url: "/kaboul-express"
    }
  ];

  // Main navigation menu items
  const menuItems = [
    {
      label: 'MENUS',
      link: '/menus',
      submenu: [
        { name: "Lunch Menu", url: "/menus/lunch" },
        { name: "Dinner Menu", url: "/menus/dinner" },
        { name: "Tasting Menu", url: "/menus/tasting" },
        { name: "Dessert Menu", url: "/menus/dessert" },
        { name: "Wine List", url: "/menus/wine" }
      ]
    },
    {
      label: 'PRIVATE DINING',
      link: '/private-dining',
      submenu: [
        { name: "The Arch", url: "/private-dining/arch" },
        { name: "The Gallery", url: "/private-dining/gallery" },
        { name: "Exclusive Hire", url: "/private-dining/exclusive-hire" }
      ]
    },
    {
      label: 'GIFT VOUCHERS',
      link: '/gift-vouchers',
      submenu: null
    },
    {
      label: 'WEDDINGS',
      link: '/weddings',
      submenu: null
    },
    {
      label: 'OUR STORY',
      link: '/our-story',
      submenu: [
        { name: "About Us", url: "/our-story/about" },
        { name: "Our Team", url: "/our-story/team" },
        { name: "Our Philosophy", url: "/our-story/philosophy" }
      ]
    },
    {
      label: 'CONTACT',
      link: '/contact',
      submenu: null
    }
  ];

  // Add scroll event listener for sticky header
  useEffect(() => {
    const handleScroll = () => {
      setIsSticky(window.pageYOffset > 80);
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  // Close mobile menu when resizing to desktop
  useEffect(() => {
    const handleResize = () => {
      if (window.innerWidth > 991 && mobileMenuOpen) {
        setMobileMenuOpen(false);
      }
    };

    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, [mobileMenuOpen]);

  // Handle body scroll locking when mobile menu is open
  useEffect(() => {
    if (mobileMenuOpen) {
      document.body.style.overflow = 'hidden';
    } else {
      document.body.style.overflow = '';
    }

    return () => {
      document.body.style.overflow = '';
    };
  }, [mobileMenuOpen]);

  // Toggle mobile menu
  const toggleMobileMenu = () => {
    setMobileMenuOpen(!mobileMenuOpen);
  };

  // Handle dropdown toggle
  const handleDropdownToggle = (index) => {
    setActiveDropdown(activeDropdown === index ? null : index);
  };

  // Handle navigation without React Router
  const handleNavigation = (e, path) => {
    e.preventDefault();
    console.log(`Navigation to ${path} would happen here`);
    // In a real app with React Router, you would use history.push(path)

    // Close mobile menu when navigating
    if (mobileMenuOpen) {
      setMobileMenuOpen(false);
    }
  };

  return (
    <header className={`site-header ${isSticky ? 'sticky' : ''}`}>
      <div className="header-container">
        {/* Top Bar with Contact & Social Links */}
        <div className="header-top-bar">
          <div className="contact-info">
            <a href="tel:+442071234567" className="phone-link">
              <i className="icon-phone"></i> +44 (0)20 7123 4567
            </a>
            <span className="address-short">204 Chelsea Street, London SW3 6LG</span>
          </div>
          <div className="social-links">
            <a href="https://instagram.com/kaboulrestaurant" aria-label="Instagram" target="_blank" rel="noopener noreferrer">
              <i className="icon-instagram"></i>
            </a>
            <a href="https://facebook.com/kaboulrestaurant" aria-label="Facebook" target="_blank" rel="noopener noreferrer">
              <i className="icon-facebook"></i>
            </a>
            <a href="https://twitter.com/kaboulrestaurant" aria-label="Twitter" target="_blank" rel="noopener noreferrer">
              <i className="icon-twitter"></i>
            </a>
          </div>
        </div>

        {/* Main Header Navigation */}
        <div className="main-header">
          <div className="logo-container">
            <a href="/" className="logo">
              <h1>KABOUL GOURMET</h1>
            </a>
          </div>

          <button 
            className={`mobile-menu-toggle ${mobileMenuOpen ? 'active' : ''}`}
            onClick={toggleMobileMenu}
            aria-expanded={mobileMenuOpen}
            aria-label="Toggle navigation menu"
          >
            <span className="bar"></span>
            <span className="bar"></span>
            <span className="bar"></span>
          </button>

          <nav className={`main-navigation ${mobileMenuOpen ? 'active' : ''}`}>
            <ul className="primary-menu">
              {menuItems.map((item, index) => (
                <li 
                  key={index} 
                  className={`menu-item ${item.submenu ? 'has-dropdown' : ''} ${activeDropdown === index ? 'active' : ''}`}
                >
                  <a 
                    href={item.link}
                    onClick={(e) => item.submenu && handleDropdownToggle(index)}
                    className="menu-link"
                  >
                    {item.label}
                    {item.submenu && <span className="dropdown-arrow"></span>}
                  </a>

                  {item.submenu && (
                    <ul className="dropdown-menu">
                      {item.submenu.map((subItem, subIndex) => (
                        <li key={subIndex} className="dropdown-item">
                          <a href={subItem.url} className="dropdown-link">{subItem.name}</a>
                        </li>
                      ))}
                    </ul>
                  )}
                </li>
              ))}
            </ul>

            <a href="/reservations" className="reservation-btn">BOOK A TABLE</a>
          </nav>
        </div>
      </div>

      {/* Restaurant Selector Dropdown */}
      <div className="restaurant-selector">
        <div 
          className={`current-restaurant ${activeDropdown === 'restaurants' ? 'active' : ''}`}
          onClick={() => handleDropdownToggle('restaurants')}
        >
          <span>Kaboul Gourmet</span>
          <span className="dropdown-arrow"></span>
        </div>

        {activeDropdown === 'restaurants' && (
          <ul className="restaurant-list">
            {restaurants.map((restaurant, index) => (
              <li key={index} className="restaurant-item">
                <a href={restaurant.url} className="restaurant-link">
                  <strong>{restaurant.name}</strong>
                  <span>{restaurant.location}</span>
                </a>
              </li>
            ))}
          </ul>
        )}
      </div>
    </header>
  );
};

export default Header;
