import React from 'react';
import { motion } from 'framer-motion';

/**
 * AnimatedImage Component
 * 
 * A wrapper component that adds subtle animations to images
 * 
 * @param {string} src - Image source URL
 * @param {string} alt - Image alt text
 * @param {string} className - Additional CSS classes
 * @param {string} effect - Animation effect type: 'zoom', 'tilt', or 'shine'
 */
const AnimatedImage = ({ 
  src, 
  alt = '', 
  className = '', 
  effect = 'zoom',
  ...props 
}) => {
  // Define hover animations
  const hoverEffects = {
    zoom: {
      scale: 1.05,
      transition: { duration: 0.5, ease: "easeOut" }
    },
    tilt: {
      rotateX: 5, 
      rotateY: 5,
      transition: { duration: 0.3, ease: "easeOut" }
    },
    shine: {
      filter: "brightness(1.1)",
      transition: { duration: 0.3, ease: "easeOut" }
    }
  };

  const containerClass = `animated-image-container ${effect === 'shine' ? 'shine-effect' : ''} ${className}`;
  
  return (
    <div className={containerClass}>
      <motion.img
        src={src}
        alt={alt}
        className="animated-image"
        initial={{ scale: 1 }}
        whileHover={hoverEffects[effect] || hoverEffects.zoom}
        {...props}
      />
    </div>
  );
};

// Add CSS for the shine effect
const shineStyles = `
  .animated-image-container {
    position: relative;
    overflow: hidden;
    display: inline-block;
  }
  
  .animated-image-container.shine-effect::before {
    content: '';
    position: absolute;
    top: 0;
    left: -75%;
    width: 50%;
    height: 100%;
    background: linear-gradient(
      to right,
      rgba(255, 255, 255, 0) 0%,
      rgba(255, 255, 255, 0.3) 100%
    );
    transform: skewX(-25deg);
    z-index: 1;
    opacity: 0;
    transition: opacity 0.3s ease;
  }
  
  .animated-image-container.shine-effect:hover::before {
    animation: shine 1s ease;
    opacity: 1;
  }
  
  @keyframes shine {
    100% {
      left: 125%;
    }
  }
  
  .animated-image {
    display: block;
    max-width: 100%;
    height: auto;
  }
`;

// Add the styles to the document head
if (typeof document !== 'undefined') {
  const styleEl = document.createElement('style');
  styleEl.type = 'text/css';
  styleEl.appendChild(document.createTextNode(shineStyles));
  document.head.appendChild(styleEl);
}

export default AnimatedImage;
