import React, { useState, useEffect } from 'react';
import './TopNav.css';

const TopNav = ({ onOpenMenu }) => {
  const [scrolled, setScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 50) {
        setScrolled(true);
      } else {
        setScrolled(false);
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <>
      <div className={`top-nav ${scrolled ? 'scrolled' : ''}`}>
        <div className="nav-container">
          <div className="nav-left">
            <button className="locations-btn" onClick={onOpenMenu}>
              <span className="hamburger-icon">
                <span className="line"></span>
                <span className="line"></span>
                <span className="line"></span>
              </span>
              <span className="locations-text">LOCATIONS</span>
            </button>
          </div>
          
          <div className="nav-center">
            <a href="/" className="logo">KABOUL GOURMET</a>
          </div>
          
          <div className="nav-right">
            <a href="tel:020-1234-5678" className="phone">020 1234 5678</a>
            <a href="#book" className="book-now">BOOK NOW</a>
          </div>
        </div>
      </div>

      <div className={`secondary-nav ${scrolled ? 'scrolled' : ''}`}>
        <div className="nav-container">
          <nav className="menu-nav">
            <ul>
              <li><a href="#menus">MENUS</a></li>
              <li><a href="#whats-on">WHAT'S ON</a></li>
              <li><a href="#private-dining">PRIVATE DINING</a></li>
              <li><a href="#about">ABOUT</a></li>
              <li><a href="#reviews">REVIEWS</a></li>
              <li><a href="#contact">CONTACT</a></li>
              <li><a href="#newsletter">NEWSLETTER</a></li>
            </ul>
          </nav>
        </div>
      </div>
    </>
  );
};

export default TopNav;
