import React, { useState } from 'react';
import { motion } from 'framer-motion';
import AnimatedSection from '../common/AnimatedSection';
import AnimatedElement from '../common/AnimatedElement';
import AnimatedImage from '../common/AnimatedImage';
import './SeasonalFresh.css';

const SeasonalFresh = () => {
  const [activeTestimonial, setActiveTestimonial] = useState(0);

  const testimonials = [
    {
      quote: "The Sunday lunch complete with children's menu â€“ is excellent value.",
      author: "Great British Chefs"
    },
    {
      quote: "Buzzing with excitement and glamour, with a mixed crowd of artists and media characters.",
      author: "Great British Chefs"
    },
    {
      quote: "The best Afghan cuisine in the heart of London.",
      author: "Hot Dinners"
    }
  ];

  const handleIndicatorClick = (index) => {
    setActiveTestimonial(index);
  };

  // Animation variants
  const contentVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.2,
        delayChildren: 0.1
      }
    }
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: {
      opacity: 1,
      y: 0,
      transition: { duration: 0.6, ease: "easeOut" }
    }
  };

  return (
    <AnimatedSection id="seasonal" className="seasonal-section">
      <div className="seasonal-grid">
        <motion.div 
          className="seasonal-image"
          initial={{ opacity: 0, scale: 0.95 }}
          whileInView={{ opacity: 1, scale: 1 }}
          viewport={{ once: true, amount: 0.3 }}
          transition={{ duration: 0.8 }}
        >
          <div className="food-presentation">
            <motion.div 
              className="plate-circle"
              initial={{ scale: 0.8, opacity: 0 }}
              whileInView={{ scale: 1, opacity: 1 }}
              viewport={{ once: true }}
              transition={{ duration: 0.5, delay: 0.2 }}
            ></motion.div>
            <motion.div 
              className="food-item food-main"
              initial={{ y: 20, opacity: 0 }}
              whileInView={{ y: 0, opacity: 1 }}
              viewport={{ once: true }}
              transition={{ duration: 0.5, delay: 0.4 }}
            ></motion.div>
            <motion.div 
              className="food-item food-garnish"
              initial={{ scale: 0, opacity: 0 }}
              whileInView={{ scale: 1, opacity: 1 }}
              viewport={{ once: true }}
              transition={{ duration: 0.5, delay: 0.6 }}
            ></motion.div>
            <motion.div 
              className="food-item food-sauce"
              initial={{ scale: 0, opacity: 0 }}
              whileInView={{ scale: 1, opacity: 1 }}
              viewport={{ once: true }}
              transition={{ duration: 0.5, delay: 0.7 }}
            ></motion.div>
            <motion.div 
              className="pouring-sauce"
              initial={{ height: 0, opacity: 0 }}
              whileInView={{ height: "100%", opacity: 1 }}
              viewport={{ once: true }}
              transition={{ duration: 1, delay: 0.8 }}
            ></motion.div>
          </div>
        </motion.div>

        <motion.div 
          className="seasonal-content"
          variants={contentVariants}
          initial="hidden"
          whileInView="visible"
          viewport={{ once: true, amount: 0.3 }}
        >
          <div className="content-wrapper">
            <motion.h2 variants={itemVariants} className="section-title">SEASONAL FRESH</motion.h2>
            <motion.div variants={itemVariants} className="divider"></motion.div>

            <motion.h3 variants={itemVariants} className="section-subtitle">Only the very best ingredients</motion.h3>

            <motion.p variants={itemVariants} className="seasonal-description">
              Our menus at Kaboul Gourmet celebrate only the best in produce of the season. With frequently changing menus our Head Chef continues to adapt our offering to ensure we only serve our guests the very best.
            </motion.p>

            <AnimatedElement 
              as="a" 
              href="#menus" 
              className="seasonal-button" 
              hoverEffect="scale"
              variants={itemVariants}
            >
              MENUS
            </AnimatedElement>

            <motion.div 
              className="testimonial-container"
              variants={itemVariants}
            >
              <div className="quote-icon">&ldquo;</div>
              <div className="testimonial-content">
                {testimonials.map((testimonial, index) => (
                  <motion.div 
                    key={index} 
                    className={`testimonial ${index === activeTestimonial ? 'active' : ''}`}
                    initial={{ opacity: 0, x: index === activeTestimonial ? 0 : 30 }}
                    animate={{ 
                      opacity: index === activeTestimonial ? 1 : 0,
                      x: index === activeTestimonial ? 0 : 30
                    }}
                    transition={{ duration: 0.5 }}
                  >
                    <p>{testimonial.quote}</p>
                    <cite>â€” {testimonial.author}</cite>
                  </motion.div>
                ))}
              </div>

              <div className="testimonial-indicators">
                {testimonials.map((_, index) => (
                  <AnimatedElement 
                    as="button" 
                    key={index} 
                    className={`indicator ${index === activeTestimonial ? 'active' : ''}`}
                    onClick={() => handleIndicatorClick(index)}
                    aria-label={`Testimonial ${index + 1}`}
                    hoverEffect="scale"
                  />
                ))}
              </div>
            </motion.div>
          </div>
        </motion.div>
      </div>
    </AnimatedSection>
  );
};

export default SeasonalFresh;
