import React from 'react';
import { motion } from 'framer-motion';

/**
 * AnimatedSection Component
 * 
 * A wrapper component that adds smooth animations to sections when they come into view
 * 
 * @param {string} className - Additional CSS classes
 * @param {string} id - Section ID
 * @param {node} children - Section content
 * @param {object} animation - Custom animation settings (optional)
 */
const AnimatedSection = ({ 
  className = '', 
  id, 
  children,
  animation = {}, 
  ...props 
}) => {
  // Default animation settings
  const defaultAnimation = {
    hidden: { 
      opacity: 0, 
      y: 20 
    },
    visible: { 
      opacity: 1, 
      y: 0,
      transition: { 
        duration: 0.6, 
        ease: "easeOut" 
      }
    }
  };

  // Merge default with any custom animations
  const finalAnimation = {
    hidden: { ...defaultAnimation.hidden, ...animation.hidden },
    visible: { 
      ...defaultAnimation.visible, 
      ...animation.visible,
      transition: { 
        ...defaultAnimation.visible.transition, 
        ...(animation.visible?.transition || {}) 
      }
    }
  };

  return (
    <motion.section
      id={id}
      className={`animated-section ${className}`}
      initial="hidden"
      whileInView="visible"
      viewport={{ once: true, amount: 0.15 }}
      variants={finalAnimation}
      {...props}
    >
      {children}
    </motion.section>
  );
};

export default AnimatedSection;
