import React, { useState, useEffect, useRef } from 'react';
import './HeroSlider.css';

const HeroSlider = () => {
  const [activeSlide, setActiveSlide] = useState(0);
  const [animating, setAnimating] = useState(false);
  const intervalRef = useRef(null);
  
  const [slides] = useState([
    {
      image: 'https://dummyimage.com/1920x1080/000/fff&text=Restaurant+Interior',
      title: 'AUTHENTIC AFGHAN CUISINE',
      subtitle: 'Experience the flavors of Kaboul in the heart of London',
      button: 'BOOK NOW',
      buttonLink: '#book',
      align: 'center' // text alignment: left, center, right
    },
    {
      image: 'https://dummyimage.com/1920x1080/1a1a1a/fff&text=Special+Event',
      title: 'SAFFRON SPICE DINNER',
      date: '16th November 2023',
      subtitle: 'A special evening celebrating Afghanistan\'s culinary treasures',
      button: 'DISCOVER MORE',
      buttonLink: '#events',
      align: 'left'
    },
    {
      image: 'https://dummyimage.com/1920x1080/2a2a2a/fff&text=Private+Dining',
      title: 'PRIVATE DINING',
      subtitle: 'Create unforgettable moments in our exclusive spaces',
      button: 'EXPLORE VENUES',
      buttonLink: '#private',
      align: 'right'
    },
    {
      image: 'https://dummyimage.com/1920x1080/3a3a3a/fff&text=Traditional+Feast',
      title: 'TRADITIONAL AFGHAN FEAST',
      date: '30th March 2024',
      subtitle: 'Join us for a celebration of Afghan culture and cuisine',
      button: 'DISCOVER MORE',
      buttonLink: '#feast',
      align: 'left'
    },
    {
      image: 'https://dummyimage.com/1920x1080/4a4a4a/fff&text=Lunch+Menu',
      title: 'LUNCH SET MENU',
      subtitle: 'Indulge in a premium Afghan lunch experience with our 3-course set menu',
      button: 'VIEW MENU',
      buttonLink: '#lunch',
      align: 'center'
    }
  ]);

  const startAutoSlide = () => {
    intervalRef.current = setInterval(() => {
      if (!animating) {
        goToNextSlide();
      }
    }, 6000);
  };

  const stopAutoSlide = () => {
    if (intervalRef.current) {
      clearInterval(intervalRef.current);
    }
  };

  useEffect(() => {
    startAutoSlide();
    
    return () => stopAutoSlide();
  }, [animating]);

  const goToNextSlide = () => {
    setAnimating(true);
    setActiveSlide(current => (current + 1) % slides.length);
    
    // Reset animating state after transition completes
    setTimeout(() => {
      setAnimating(false);
    }, 1000);
  };

  const goToPrevSlide = () => {
    setAnimating(true);
    setActiveSlide(current => (current === 0 ? slides.length - 1 : current - 1));
    
    // Reset animating state after transition completes
    setTimeout(() => {
      setAnimating(false);
    }, 1000);
  };

  const handleIndicatorClick = (index) => {
    if (index !== activeSlide && !animating) {
      setAnimating(true);
      setActiveSlide(index);
      
      // Reset animating state after transition completes
      setTimeout(() => {
        setAnimating(false);
      }, 1000);
    }
  };

  return (
    <section className="hero-slider">
      <div className="slides-container">
        {slides.map((slide, index) => (
          <div 
            key={index} 
            className={`slide ${index === activeSlide ? 'active' : ''} ${
              index === (activeSlide === 0 ? slides.length - 1 : activeSlide - 1) ? 'prev' : ''
            } ${
              index === (activeSlide === slides.length - 1 ? 0 : activeSlide + 1) ? 'next' : ''
            }`}
            style={{ 
              backgroundImage: `linear-gradient(rgba(0, 0, 0, 0.3), rgba(0, 0, 0, 0.3)), url(${slide.image})` 
            }}
          >
            <div className={`slide-content align-${slide.align}`}>
              <div className="slide-text">
                {slide.date && <div className="event-date">{slide.date}</div>}
                <h1>{slide.title}</h1>
                <p>{slide.subtitle}</p>
                <a href={slide.buttonLink} className="slide-button">{slide.button}</a>
              </div>
            </div>
          </div>
        ))}
      </div>
      
      <div className="slider-controls">
        <button className="slider-arrow slider-arrow-left" onClick={goToPrevSlide}>
          <span className="arrow-icon">&#10094;</span>
        </button>
        <button className="slider-arrow slider-arrow-right" onClick={goToNextSlide}>
          <span className="arrow-icon">&#10095;</span>
        </button>
      </div>
      
      <div className="slider-indicators">
        {slides.map((_, index) => (
          <button 
            key={index} 
            className={`indicator ${index === activeSlide ? 'active' : ''}`}
            onClick={() => handleIndicatorClick(index)}
            aria-label={`Go to slide ${index + 1}`}
          />
        ))}
      </div>
    </section>
  );
};

export default HeroSlider;
