import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence, useReducedMotion } from 'framer-motion';
import AnimatedElement from '../common/AnimatedElement';
import './HeroSlider.css';

// Image preloading utility function
const preloadImage = (src) => {
  return new Promise((resolve, reject) => {
    const img = new Image();
    img.onload = () => resolve(img);
    img.onerror = reject;
    img.src = src;
  });
};

const HeroSlider = () => {
  const [activeSlide, setActiveSlide] = useState(0);
  const [isLoading, setIsLoading] = useState(true);
  const [loadedImages, setLoadedImages] = useState({});
  const prefersReducedMotion = useReducedMotion();
  const [isMobile, setIsMobile] = useState(false);

  // Define slide backgrounds with both placeholder and full images
  const [slides] = useState([
    {
      background: '#1a1a1a',
      backgroundImage: '/images/hero-slide-1.jpg', // These would be your actual image paths
      placeholderImage: '/images/hero-slide-1-small.jpg', // Small placeholder version
      title: 'AUTHENTIC AFGHAN CUISINE',
      subtitle: 'Experience the flavors of Kaboul in the heart of London',
      button: 'BOOK A TABLE',
      buttonLink: '#book'
    },
    {
      background: '#2a2a2a',
      backgroundImage: '/images/hero-slide-2.jpg',
      placeholderImage: '/images/hero-slide-2-small.jpg',
      title: 'SAFFRON SPICE DINNER',
      date: '16th November 2023',
      subtitle: 'A special evening celebrating Afghanistan\'s culinary treasures',
      button: 'DISCOVER MORE',
      buttonLink: '#events'
    },
    {
      background: '#3a3a3a',
      backgroundImage: '/images/hero-slide-3.jpg',
      placeholderImage: '/images/hero-slide-3-small.jpg',
      title: 'PRIVATE DINING',
      subtitle: 'Create unforgettable moments in our exclusive spaces',
      button: 'EXPLORE VENUES',
      buttonLink: '#private'
    },
    {
      background: '#4a4a4a',
      backgroundImage: '/images/hero-slide-4.jpg',
      placeholderImage: '/images/hero-slide-4-small.jpg',
      title: 'TRADITIONAL AFGHAN FEAST',
      date: '30th March 2024',
      subtitle: 'Join us for a celebration of Afghan culture and cuisine',
      button: 'DISCOVER MORE',
      buttonLink: '#feast'
    },
    {
      background: '#5a5a5a',
      backgroundImage: '/images/hero-slide-5.jpg',
      placeholderImage: '/images/hero-slide-5-small.jpg',
      title: 'LUNCH SET MENU',
      subtitle: 'Indulge in a premium Afghan lunch experience with our 3-course set menu',
      button: 'VIEW MENU',
      buttonLink: '#lunch'
    }
  ]);

  useEffect(() => {
    // Check for mobile devices
    const checkDevice = () => {
      setIsMobile(window.innerWidth <= 768);
    };

    checkDevice();
    window.addEventListener('resize', checkDevice);

    return () => {
      window.removeEventListener('resize', checkDevice);
    };
  }, []);

  useEffect(() => {
    // Preload the hero images for smoother transitions
    const preloadHeroImages = async () => {
      setIsLoading(true);

      try {
        // For optimal performance, preload first 2 images immediately, 
        // then load the rest in the background
        const imagesToPreload = isMobile
          ? slides.map(slide => slide.placeholderImage) // Use placeholders on mobile
          : slides.map(slide => slide.backgroundImage);

        // First preload the current and next image
        const initialPreloads = imagesToPreload.slice(0, 2).map(src => preloadImage(src));
        const loadedInitialImages = await Promise.all(initialPreloads);

        // Track which images are loaded
        const initialLoadedObj = {};
        loadedInitialImages.forEach((img, i) => {
          initialLoadedObj[imagesToPreload[i]] = true;
        });

        setLoadedImages(initialLoadedObj);
        setIsLoading(false);

        // Then load the rest in the background
        const remainingPreloads = imagesToPreload.slice(2).map(src => preloadImage(src));
        const loadedRemainingImages = await Promise.all(remainingPreloads);

        // Update loaded images object
        const allLoadedObj = { ...initialLoadedObj };
        loadedRemainingImages.forEach((img, i) => {
          allLoadedObj[imagesToPreload[i + 2]] = true;
        });

        setLoadedImages(allLoadedObj);
      } catch (error) {
        console.error('Error preloading images:', error);
        setIsLoading(false); 
      }
    };

    preloadHeroImages();
  }, [slides, isMobile]);

  useEffect(() => {
    // Auto-rotation interval - optimize for performance
    // Skip rotation if the tab is hidden or user prefers reduced motion
    let interval = null;

    if (!document.hidden && !prefersReducedMotion) {
      interval = setInterval(() => {
        setActiveSlide(current => (current + 1) % slides.length);
      }, isMobile ? 6000 : 5000); // Slightly longer interval on mobile
    }

    // Clean up and handle visibility changes
    const handleVisibilityChange = () => {
      if (document.hidden && interval) {
        clearInterval(interval);
        interval = null;
      } else if (!document.hidden && !interval && !prefersReducedMotion) {
        interval = setInterval(() => {
          setActiveSlide(current => (current + 1) % slides.length);
        }, isMobile ? 6000 : 5000);
      }
    };

    document.addEventListener('visibilitychange', handleVisibilityChange);

    return () => {
      if (interval) clearInterval(interval);
      document.removeEventListener('visibilitychange', handleVisibilityChange);
    };
  }, [slides.length, prefersReducedMotion, isMobile]);

  const handleIndicatorClick = (index) => {
    setActiveSlide(index);
  };

  // Optimized animation variants for better performance
  const slideVariants = {
    enter: (direction) => ({
      x: direction > 0 ? '100%' : '-100%',
      opacity: 0
    }),
    center: {
      x: 0,
      opacity: 1,
      transition: {
        duration: isMobile ? 0.5 : 0.8,
        ease: [0.16, 1, 0.3, 1], // Custom ease curve for elegant motion
      }
    },
    exit: (direction) => ({
      x: direction < 0 ? '100%' : '-100%',
      opacity: 0,
      transition: {
        duration: isMobile ? 0.4 : 0.8,
        ease: [0.16, 1, 0.3, 1],
      }
    })
  };

  // Simplify animations on reduced motion preference
  const accessibleSlideVariants = {
    enter: { opacity: 0 },
    center: {
      opacity: 1,
      transition: { duration: 0.5 }
    },
    exit: {
      opacity: 0,
      transition: { duration: 0.5 }
    }
  };

  // Content animation - optimized for performance
  const contentVariants = {
    hidden: { opacity: 0, y: isMobile ? 15 : 30 },
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        duration: isMobile ? 0.5 : 0.7,
        delay: isMobile ? 0.1 : 0.2,
        staggerChildren: isMobile ? 0.1 : 0.15,
        delayChildren: 0.1
      }
    }
  };

  const itemVariants = {
    hidden: { opacity: 0, y: isMobile ? 10 : 20 },
    visible: {
      opacity: 1,
      y: 0,
      transition: { duration: isMobile ? 0.3 : 0.5 }
    }
  };

  // Use simplified animations for reduced motion
  const finalSlideVariants = prefersReducedMotion ? accessibleSlideVariants : slideVariants;

  // Get background style for a slide
  const getSlideBackground = (slide) => {
    // Use solid color while loading as fallback
    if (isLoading) {
      return { backgroundColor: slide.background };
    }

    // Choose appropriate image based on device and loading state
    const bgImageToUse = isMobile ? slide.placeholderImage : slide.backgroundImage;
    const isImageLoaded = loadedImages[bgImageToUse];

    return {
      backgroundColor: slide.background,
      backgroundImage: isImageLoaded ? `url(${bgImageToUse})` : 'none',
      backgroundSize: 'cover',
      backgroundPosition: 'center'
    };
  };

  return (
    <motion.section 
      className="hero-slider"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 0.8 }}
    >
      <AnimatePresence initial={false} mode="wait">
        {slides.map((slide, index) => (
          index === activeSlide && (
            <motion.div 
              key={`slide-${index}`}
              className="slide active"
              style={getSlideBackground(slide)}
              initial="enter"
              animate="center"
              exit="exit"
              variants={finalSlideVariants}
              custom={activeSlide > (activeSlide - 1 + slides.length) % slides.length ? 1 : -1}
            >
              <motion.div 
                className="slide-content"
                initial="hidden"
                animate="visible"
                variants={contentVariants}
              >
                {slide.date && (
                  <motion.div className="event-date" variants={itemVariants}>
                    {slide.date}
                  </motion.div>
                )}
                <motion.h1 variants={itemVariants}>{slide.title}</motion.h1>
                <motion.p variants={itemVariants}>{slide.subtitle}</motion.p>
                <AnimatedElement 
                  as="a" 
                  href={slide.buttonLink} 
                  className="slide-button" 
                  hoverEffect="scale"
                  variants={itemVariants}
                >
                  {slide.button}
                </AnimatedElement>
              </motion.div>
            </motion.div>
          )
        ))}
      </AnimatePresence>

      <motion.div 
        className="slider-indicators"
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.8, duration: 0.5 }}
      >
        {slides.map((_, index) => (
          <AnimatedElement 
            as="button"
            key={index} 
            className={`indicator ${index === activeSlide ? 'active' : ''}`}
            onClick={() => handleIndicatorClick(index)}
            hoverEffect="scale"
            whileTap={{ scale: 0.95 }}
          />
        ))}
      </motion.div>
    </motion.section>
  );
};

export default HeroSlider;
