import React, { useState, useEffect, useRef } from 'react';
import './Header.css';

const Header = () => {
  // State for sticky header
  const [isSticky, setIsSticky] = useState(false);

  // State for active dropdowns
  const [activeDropdown, setActiveDropdown] = useState(null);

  // State for mobile menu
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);

  // State for active mobile dropdowns
  const [activeMobileDropdown, setActiveMobileDropdown] = useState(null);

  // Refs for detecting clicks outside dropdowns
  const dropdownRefs = useRef([]);

  // Restaurant locations array
  const restaurants = [
    {
      name: 'Galvin La Chapelle',
      address: '35 Spital Square, London, E1 6DY',
      link: '/restaurants/la-chapelle'
    },
    {
      name: 'Galvin Bistrot & Bar',
      address: '35 Spital Square, London, E1 6DY',
      link: '/restaurants/bistrot-bar'
    },
    {
      name: 'Galvin Bar & Grill',
      address: 'Kimpton Fitzroy, Bloomsbury, WC1B 5BE',
      link: '/restaurants/bar-grill'
    },
    {
      name: 'Galvin Green Man',
      address: 'Main Road, Howe Street, Essex, CM3 1BG',
      link: '/restaurants/green-man'
    }
  ];

  // Menu items array - Refined menu structure to match Galvin's style
  const menuItems = [
    {
      label: 'MENUS',
      link: '/menus',
      hasDropdown: true,
      dropdownItems: [
        { label: 'Ã€ La Carte', link: '/menus/a-la-carte' },
        { label: 'Tasting Menu', link: '/menus/tasting-menu' },
        { label: 'Wine List', link: '/menus/wine-list' },
        { label: 'Sunday Lunch', link: '/menus/sunday-lunch' }
      ]
    },
    {
      label: 'PRIVATE DINING',
      link: '/private-dining',
      hasDropdown: true,
      dropdownItems: [
        { label: 'Club Room', link: '/private-dining/club-room' },
        { label: 'Gallery', link: '/private-dining/gallery' },
        { label: 'The Crypt', link: '/private-dining/crypt' },
      ]
    },
    {
      label: 'GIFT VOUCHERS',
      link: '/gift-vouchers',
      hasDropdown: false
    },
    {
      label: 'WEDDINGS',
      link: '/weddings',
      hasDropdown: false
    },
    {
      label: 'OUR STORY',
      link: '/our-story',
      hasDropdown: false
    },
    {
      label: 'CONTACT',
      link: '/contact',
      hasDropdown: false
    }
  ];

  // Add scroll event listener for sticky header
  useEffect(() => {
    const handleScroll = () => {
      setIsSticky(window.pageYOffset > 80);
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  // Add event listener for clicks outside dropdown
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (activeDropdown !== null && !dropdownRefs.current[activeDropdown]?.contains(event.target)) {
        setActiveDropdown(null);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);

    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [activeDropdown]);

  // Close mobile menu when resizing to desktop
  useEffect(() => {
    const handleResize = () => {
      if (window.innerWidth > 991 && mobileMenuOpen) {
        setMobileMenuOpen(false);
        setActiveMobileDropdown(null);
      }
    };

    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, [mobileMenuOpen]);

  // Handle body scroll locking when mobile menu is open
  useEffect(() => {
    if (mobileMenuOpen) {
      document.body.style.overflow = 'hidden';
    } else {
      document.body.style.overflow = '';
    }

    return () => {
      document.body.style.overflow = '';
    };
  }, [mobileMenuOpen]);

  // Toggle desktop dropdown function
  const toggleDropdown = (index) => {
    setActiveDropdown(activeDropdown === index ? null : index);
  };

  // Toggle mobile dropdown function
  const toggleMobileDropdown = (index) => {
    setActiveMobileDropdown(activeMobileDropdown === index ? null : index);
  };

  // Toggle mobile menu
  const toggleMobileMenu = () => {
    setMobileMenuOpen(!mobileMenuOpen);
    // Reset active dropdowns when toggling mobile menu
    setActiveMobileDropdown(null);
  };

  // Handle navigation without React Router
  const handleNavigation = (e, path) => {
    e.preventDefault();
    console.log(`Navigation to ${path} would happen here`);
    // In a real app with React Router, you would use history.push(path)
  };

  return (
    <header className={`site-header ${isSticky ? 'sticky' : ''}`}>
      <div className="header-container container">
        {/* Logo (Left) - Enhanced for sophistication */}
        <div className="header-logo">
          <a 
            href="/" 
            className="logo-link"
            onClick={(e) => handleNavigation(e, "/")}
          >
            <div className="logo-text-container">
              <span className="logo-text-main">KABOUL</span>
              <span className="logo-separator"></span>
              <span className="logo-text-tagline">GOURMET</span>
            </div>
          </a>
        </div>

        {/* Main Navigation (Center) */}
        <nav className="main-navigation">
          <ul className="nav-menu">
            {menuItems.map((item, index) => (
              <li 
                key={index} 
                className={`nav-item ${item.hasDropdown ? 'has-dropdown' : ''}`}
                ref={el => dropdownRefs.current[index] = el}
              >
                {item.hasDropdown ? (
                  <>
                    <a 
                      href={item.link} 
                      onClick={(e) => {
                        e.preventDefault();
                        toggleDropdown(index);
                      }}
                      className={activeDropdown === index ? 'active' : ''}
                    >
                      {item.label}
                    </a>
                    <div className={`dropdown-content ${activeDropdown === index ? 'active' : ''}`}>
                      <ul>
                        {item.dropdownItems.map((dropdownItem, i) => (
                          <li key={i}>
                            <a 
                              href={dropdownItem.link} 
                              onClick={(e) => handleNavigation(e, dropdownItem.link)}
                            >
                              {dropdownItem.label}
                            </a>
                          </li>
                        ))}
                      </ul>
                    </div>
                  </>
                ) : (
                  <a 
                    href={item.link}
                    onClick={(e) => handleNavigation(e, item.link)}
                  >
                    {item.label}
                  </a>
                )}
              </li>
            ))}
          </ul>
        </nav>

        {/* Book Now Button (Right) */}
        <div className="header-cta">
          <a 
            href="/book" 
            className="btn btn-primary"
            onClick={(e) => handleNavigation(e, "/book")}
          >
            BOOK NOW
          </a>
        </div>

        {/* Mobile Menu Toggle */}
        <div className="mobile-menu-toggle" onClick={toggleMobileMenu}>
          <div className={`hamburger ${mobileMenuOpen ? 'active' : ''}`}>
            <span className="bar"></span>
            <span className="bar"></span>
            <span className="bar"></span>
          </div>
        </div>
      </div>

      {/* Mobile Menu - Improved with proper toggle functionality */}
      <div className={`mobile-menu ${mobileMenuOpen ? 'active' : ''}`}>
        <div className="container">
          <ul className="mobile-nav-menu">
            {menuItems.map((item, index) => (
              <li 
                key={index} 
                className={`mobile-nav-item ${activeMobileDropdown === index ? 'active' : ''}`}
              >
                {item.hasDropdown ? (
                  <>
                    <a 
                      href={item.link}
                      onClick={(e) => {
                        e.preventDefault();
                        toggleMobileDropdown(index);
                      }}
                    >
                      {item.label}
                      <span className="dropdown-indicator"></span>
                    </a>

                    <ul className="mobile-dropdown">
                      {item.dropdownItems.map((dropdownItem, i) => (
                        <li key={i} className="mobile-dropdown-item">
                          <a 
                            href={dropdownItem.link} 
                            onClick={(e) => handleNavigation(e, dropdownItem.link)}
                          >
                            {dropdownItem.label}
                          </a>
                        </li>
                      ))}
                    </ul>
                  </>
                ) : (
                  <a 
                    href={item.link}
                    onClick={(e) => handleNavigation(e, item.link)}
                  >
                    {item.label}
                  </a>
                )}
              </li>
            ))}

            <li className="mobile-nav-item mobile-nav-cta">
              <a 
                href="/book" 
                className="btn btn-primary"
                onClick={(e) => handleNavigation(e, "/book")}
              >
                BOOK NOW
              </a>
            </li>
          </ul>
        </div>
      </div>
    </header>
  );
};

export default Header;
