import React, { useState } from 'react';
import './PrivateDiningCity.css';

const PrivateDiningCity = () => {
  const [activeRoom, setActiveRoom] = useState(0);

  const diningRooms = [
    {
      name: "THE GALLERY",
      location: "The City, London",
      capacity: "Up to 16 guests",
      image: "gallery"
    },
    {
      name: "THE ARCH",
      location: "The City, London",
      capacity: "Up to 22 guests",
      image: "arch"
    },
    {
      name: "THE MEZZANINE",
      location: "The City, London",
      capacity: "Up to 30 guests",
      image: "mezzanine"
    },
    {
      name: "THE LOUNGE",
      location: "The City, London",
      capacity: "Up to 14 guests",
      image: "lounge"
    }
  ];

  const nextRoom = () => {
    setActiveRoom((activeRoom + 1) % diningRooms.length);
  };

  const prevRoom = () => {
    setActiveRoom((activeRoom - 1 + diningRooms.length) % diningRooms.length);
  };

  const titleStyle = {
    color: '#ffffff',
    textShadow: '0 2px 4px rgba(0, 0, 0, 0.3)'
  };

  const buttonStyle = {
    padding: '12px 40px',
    backgroundColor: '#fff',
    color: '#1a1a1a',
    border: '2px solid #fff',
    whiteSpace: 'nowrap',
    fontWeight: 500
  };

  return (
    <section id="private-dining-city" className="private-dining-city-section">
      <div className="private-dining-city-container">
        <div className="section-header">
          <h2 className="section-title" style={titleStyle}>PRIVATE DINING IN THE CITY OF LONDON</h2>
          <h3 className="section-subtitle">Unforgettable Experiences at Kaboul Gourmet</h3>
          <p className="section-description">
            Unforgettable experiences for any event from intimate private dining to exclusive hire
            venues upto 150 guests.
          </p>
          <a href="#rooms" className="discover-button" style={buttonStyle}>DISCOVER ROOMS</a>
        </div>

        <div className="rooms-slider-container">
          <button className="slider-arrow prev" onClick={prevRoom} aria-label="Previous room">
            <span className="arrow-icon">&#8249;</span>
          </button>

          <div className="rooms-slider">
            {diningRooms.map((room, index) => (
              <div 
                key={index} 
                className={`room-slide ${index === activeRoom ? 'active' : ''}`}
              >
                <div className={`room-image ${room.image.toLowerCase()}`}>
                  <div className="room-details">
                    <div className="room-title-container">
                      <h4 className="room-name">{room.name}</h4>
                      <p className="room-location">{room.location}</p>
                    </div>
                    <div className="room-buttons">
                      <a href="#enquire" className="room-button">GET IN TOUCH</a>
                      <a href="#explore" className="room-button">EXPLORE</a>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>

          <button className="slider-arrow next" onClick={nextRoom} aria-label="Next room">
            <span className="arrow-icon">&#8250;</span>
          </button>
        </div>

        <div className="decorative-curve"></div>
      </div>
    </section>
  );
};

export default PrivateDiningCity;
