import React, { useState, useEffect } from 'react';
import './Header.css';

const Header = () => {
  // State for sticky header
  const [isSticky, setIsSticky] = useState(false);

  // State for mobile menu
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);

  // Simplified menu items array - removed dropdowns, keeping only top-level navigation
  const menuItems = [
    {
      label: 'MENUS',
      link: '/menus'
    },
    {
      label: 'PRIVATE DINING',
      link: '/private-dining'
    },
    {
      label: 'GIFT VOUCHERS',
      link: '/gift-vouchers'
    },
    {
      label: 'WEDDINGS',
      link: '/weddings'
    },
    {
      label: 'OUR STORY',
      link: '/our-story'
    },
    {
      label: 'CONTACT',
      link: '/contact'
    }
  ];

  // Add scroll event listener for sticky header
  useEffect(() => {
    const handleScroll = () => {
      setIsSticky(window.pageYOffset > 80);
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  // Close mobile menu when resizing to desktop
  useEffect(() => {
    const handleResize = () => {
      if (window.innerWidth > 991 && mobileMenuOpen) {
        setMobileMenuOpen(false);
      }
    };

    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, [mobileMenuOpen]);

  // Handle body scroll locking when mobile menu is open
  useEffect(() => {
    if (mobileMenuOpen) {
      document.body.style.overflow = 'hidden';
    } else {
      document.body.style.overflow = '';
    }

    return () => {
      document.body.style.overflow = '';
    };
  }, [mobileMenuOpen]);

  // Toggle mobile menu
  const toggleMobileMenu = () => {
    setMobileMenuOpen(!mobileMenuOpen);
  };

  // Handle navigation without React Router
  const handleNavigation = (e, path) => {
    e.preventDefault();
    console.log(`Navigation to ${path} would happen here`);
    // In a real app with React Router, you would use history.push(path)

    // Close mobile menu when navigating
    if (mobileMenuOpen) {
      setMobileMenuOpen(false);
    }
  };

  return (
    <header className={`site-header ${isSticky ? 'sticky' : ''}`}>
      <div className="header-container container">
        {/* Logo (Left) */}
        <div className="header-logo">
          <a 
            href="/" 
            className="logo-link"
            onClick={(e) => handleNavigation(e, "/")}
          >
            <span className="logo-text">KABOUL GOURMET</span>
          </a>
        </div>

        {/* Main Navigation (Center) - Simplified without dropdowns */}
        <nav className="main-navigation">
          <ul className="nav-menu">
            {menuItems.map((item, index) => (
              <li key={index} className="nav-item">
                <a 
                  href={item.link}
                  onClick={(e) => handleNavigation(e, item.link)}
                >
                  {item.label}
                </a>
              </li>
            ))}
          </ul>
        </nav>

        {/* Book Now Button (Right) */}
        <div className="header-cta">
          <a 
            href="/book" 
            className="btn btn-primary"
            onClick={(e) => handleNavigation(e, "/book")}
          >
            BOOK NOW
          </a>
        </div>

        {/* Mobile Menu Toggle */}
        <div className="mobile-menu-toggle" onClick={toggleMobileMenu}>
          <div className={`hamburger ${mobileMenuOpen ? 'active' : ''}`}>
            <span className="bar"></span>
            <span className="bar"></span>
            <span className="bar"></span>
          </div>
        </div>
      </div>

      {/* Mobile Menu - Simplified vertical list with no dropdowns */}
      <div className={`mobile-menu ${mobileMenuOpen ? 'active' : ''}`}>
        <div className="container">
          <ul className="mobile-nav-menu">
            {menuItems.map((item, index) => (
              <li key={index} className="mobile-nav-item">
                <a 
                  href={item.link}
                  onClick={(e) => handleNavigation(e, item.link)}
                >
                  {item.label}
                </a>
              </li>
            ))}

            <li className="mobile-nav-item mobile-nav-cta">
              <a 
                href="/book" 
                className="btn btn-primary"
                onClick={(e) => handleNavigation(e, "/book")}
              >
                BOOK NOW
              </a>
            </li>
          </ul>
        </div>
      </div>
    </header>
  );
};

export default Header;
