import React, { useState, useEffect, useRef } from 'react';
import { motion, AnimatePresence, useReducedMotion } from 'framer-motion';
import AnimatedElement from '../common/AnimatedElement';
import './HeroSlider.css';

/**
 * Advanced image preloading with priority
 * This optimizes performance by intelligently preloading images
 */
const preloadImage = (src, priority = 'high') => {
  return new Promise((resolve, reject) => {
    const img = new Image();
    img.onload = () => resolve(img);
    img.onerror = reject;
    img.src = src;
    img.fetchPriority = priority;

    // Add connection hints for high priority images
    if (priority === 'high' && typeof document !== 'undefined') {
      const link = document.createElement('link');
      link.rel = 'preload';
      link.as = 'image';
      link.href = src;
      link.fetchPriority = 'high';
      document.head.appendChild(link);
    }
  });
};

const HeroSlider = () => {
  const [activeSlide, setActiveSlide] = useState(0);
  const [isLoading, setIsLoading] = useState(true);
  const [loadedImages, setLoadedImages] = useState({});
  const [imageLoadFallback, setImageLoadFallback] = useState(false);
  const sliderRef = useRef(null);
  const prefersReducedMotion = useReducedMotion();
  const [isMobile, setIsMobile] = useState(false);
  const [isPaused, setIsPaused] = useState(false);
  const intervalRef = useRef(null);

  // Define slide backgrounds with both placeholder and full images
  const [slides] = useState([
    {
      background: '#1a1a1a',
      backgroundImage: '/images/hero-slide-1.jpg', // These would be your actual image paths
      placeholderImage: '/images/hero-slide-1-small.jpg', // Small placeholder version
      title: 'AUTHENTIC AFGHAN CUISINE',
      subtitle: 'Experience the flavors of Kaboul in the heart of London',
      secondaryText: 'FINE DINING RESTAURANT',
      button: 'RESERVE A TABLE',
      buttonLink: '#book'
    },
    {
      background: '#2a2a2a',
      backgroundImage: '/images/hero-slide-2.jpg',
      placeholderImage: '/images/hero-slide-2-small.jpg',
      title: 'SEASONAL MENU',
      secondaryText: 'AUTUMN COLLECTION',
      date: '16th November 2023',
      subtitle: 'A unique dining experience celebrating the finest seasonal Afghan ingredients',
      button: 'VIEW MENU',
      buttonLink: '#events'
    },
    {
      background: '#3a3a3a',
      backgroundImage: '/images/hero-slide-3.jpg',
      placeholderImage: '/images/hero-slide-3-small.jpg',
      title: 'PRIVATE DINING',
      secondaryText: 'EXCEPTIONAL SPACES',
      subtitle: 'Create unforgettable moments in our exclusive chef\'s table and private rooms',
      button: 'ENQUIRE NOW',
      buttonLink: '#private'
    },
    {
      background: '#4a4a4a',
      backgroundImage: '/images/hero-slide-4.jpg',
      placeholderImage: '/images/hero-slide-4-small.jpg',
      title: 'CHEF\'S TASTING MENU',
      secondaryText: 'CULINARY JOURNEY',
      date: '30th March 2024',
      subtitle: 'Experience the artistry of authentic Afghan cuisine with our seven-course menu',
      button: 'DISCOVER',
      buttonLink: '#feast'
    },
    {
      background: '#5a5a5a',
      backgroundImage: '/images/hero-slide-5.jpg',
      placeholderImage: '/images/hero-slide-5-small.jpg',
      title: 'BUSINESS LUNCH',
      secondaryText: 'MONDAY TO FRIDAY',
      subtitle: 'Elevate your midday experience with our curated lunch menu',
      button: 'VIEW MENU',
      buttonLink: '#lunch'
    }
  ]);

  useEffect(() => {
    // Check for mobile devices and update state
    const checkDevice = () => {
      setIsMobile(window.innerWidth <= 768);
    };

    checkDevice();
    window.addEventListener('resize', checkDevice);

    return () => {
      window.removeEventListener('resize', checkDevice);
    };
  }, []);

  /**
   * Improved progressive image loading strategy:
   * 1. Load placeholder images immediately for all slides
   * 2. Load high-quality image for current slide with high priority 
   * 3. Preload next slide's high-quality image
   * 4. Load remaining images with low priority
   */
  useEffect(() => {
    const preloadHeroImages = async () => {
      setIsLoading(true);

      try {
        // First, load all placeholder images (small and fast)
        const placeholderImages = slides.map(slide => slide.placeholderImage);
        const placeholderLoads = placeholderImages.map(src => 
          preloadImage(src, 'low')
        );

        await Promise.all(placeholderLoads);

        // Update loaded images with placeholders
        const initialLoadedObj = {};
        placeholderImages.forEach(src => {
          initialLoadedObj[src] = true;
        });

        setLoadedImages(initialLoadedObj);
        setIsLoading(false);

        // Then preload the current slide's high quality image with high priority
        const currentImage = slides[activeSlide].backgroundImage;
        await preloadImage(currentImage, 'high');

        // Update loaded images with current high-quality image
        setLoadedImages(prev => ({
          ...prev,
          [currentImage]: true
        }));

        // Preload next slide with medium priority
        const nextSlideIndex = (activeSlide + 1) % slides.length;
        const nextImage = slides[nextSlideIndex].backgroundImage;
        await preloadImage(nextImage, 'medium');

        // Update loaded images with next high-quality image
        setLoadedImages(prev => ({
          ...prev,
          [nextImage]: true
        }));

        // Then load the rest of high quality images with low priority
        const remainingImages = slides
          .filter((_, index) => index !== activeSlide && index !== nextSlideIndex)
          .map(slide => slide.backgroundImage);

        const remainingLoads = remainingImages.map(src => 
          preloadImage(src, 'low')
        );

        // Don't await these - they'll load in the background
        Promise.all(remainingLoads)
          .then(images => {
            const allLoadedObj = { ...initialLoadedObj };
            images.forEach((_, i) => {
              allLoadedObj[remainingImages[i]] = true;
            });

            setLoadedImages(prev => ({
              ...prev,
              ...allLoadedObj
            }));
          })
          .catch(error => {
            console.error('Error loading remaining images:', error);
            // If background loads fail, enable fallbacks
            setImageLoadFallback(true);
          });

      } catch (error) {
        console.error('Error preloading critical images:', error);
        setIsLoading(false);
        setImageLoadFallback(true);
      }
    };

    preloadHeroImages();

    // Setup IntersectionObserver to pause animations when not in viewport
    if (typeof IntersectionObserver !== 'undefined' && sliderRef.current) {
      const observer = new IntersectionObserver(
        (entries) => {
          setIsPaused(!entries[0].isIntersecting);
        },
        { threshold: 0.1 }
      );

      observer.observe(sliderRef.current);

      return () => {
        if (sliderRef.current) {
          observer.unobserve(sliderRef.current);
        }
      };
    }
  }, [slides, activeSlide]);

  // Preload the next slide's image when active slide changes
  useEffect(() => {
    const preloadNextSlide = async () => {
      const nextSlideIndex = (activeSlide + 1) % slides.length;
      const nextImage = slides[nextSlideIndex].backgroundImage;

      if (!loadedImages[nextImage]) {
        try {
          await preloadImage(nextImage, 'medium');
          setLoadedImages(prev => ({
            ...prev,
            [nextImage]: true
          }));
        } catch (error) {
          console.error('Error preloading next slide:', error);
        }
      }
    };

    preloadNextSlide();
  }, [activeSlide, slides, loadedImages]);

  useEffect(() => {
    // Auto-rotation interval - optimize for performance
    // Skip rotation if the tab is hidden, slider is not in view, 
    // or user prefers reduced motion
    if (document.hidden || isPaused || prefersReducedMotion) {
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
        intervalRef.current = null;
      }
      return;
    }

    intervalRef.current = setInterval(() => {
      setActiveSlide(current => (current + 1) % slides.length);
    }, isMobile ? 6000 : 5000); // Slightly longer interval on mobile

    // Clean up and handle visibility/focus changes
    const handleVisibilityChange = () => {
      if (document.hidden || isPaused) {
        if (intervalRef.current) {
          clearInterval(intervalRef.current);
          intervalRef.current = null;
        }
      } else if (!document.hidden && !isPaused && !prefersReducedMotion) {
        if (intervalRef.current) clearInterval(intervalRef.current);

        intervalRef.current = setInterval(() => {
          setActiveSlide(current => (current + 1) % slides.length);
        }, isMobile ? 6000 : 5000);
      }
    };

    document.addEventListener('visibilitychange', handleVisibilityChange);

    return () => {
      if (intervalRef.current) clearInterval(intervalRef.current);
      document.removeEventListener('visibilitychange', handleVisibilityChange);
    };
  }, [slides.length, prefersReducedMotion, isMobile, isPaused]);

  const handleIndicatorClick = (index) => {
    setActiveSlide(index);
  };

  // Touch handling for mobile swipe
  const [touchStart, setTouchStart] = useState(0);
  const [touchEnd, setTouchEnd] = useState(0);

  const handleTouchStart = (e) => {
    setTouchStart(e.targetTouches[0].clientX);
  };

  const handleTouchMove = (e) => {
    setTouchEnd(e.targetTouches[0].clientX);
  };

  const handleTouchEnd = () => {
    if (touchStart - touchEnd > 50) {
      // Swipe left - next slide
      setActiveSlide((current) => (current + 1) % slides.length);
    }

    if (touchStart - touchEnd < -50) {
      // Swipe right - previous slide
      setActiveSlide((current) => 
        current === 0 ? slides.length - 1 : current - 1
      );
    }
  };

  // Optimized animation variants for better performance
  const slideVariants = {
    enter: (direction) => ({
      x: direction > 0 ? '100%' : '-100%',
      opacity: 0
    }),
    center: {
      x: 0,
      opacity: 1,
      transition: {
        duration: isMobile ? 0.5 : 0.8,
        ease: [0.16, 1, 0.3, 1], // Custom ease curve for elegant motion
      }
    },
    exit: (direction) => ({
      x: direction < 0 ? '100%' : '-100%',
      opacity: 0,
      transition: {
        duration: isMobile ? 0.4 : 0.8,
        ease: [0.16, 1, 0.3, 1],
      }
    })
  };

  // Simplified animations on reduced motion preference
  const accessibleSlideVariants = {
    enter: { opacity: 0 },
    center: {
      opacity: 1,
      transition: { duration: 0.5 }
    },
    exit: {
      opacity: 0,
      transition: { duration: 0.5 }
    }
  };

  // Content animation - optimized for performance
  const contentVariants = {
    hidden: { opacity: 0, y: isMobile ? 15 : 30 },
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        duration: isMobile ? 0.5 : 0.7,
        delay: isMobile ? 0.1 : 0.2,
        staggerChildren: isMobile ? 0.1 : 0.15,
        delayChildren: 0.1
      }
    }
  };

  const itemVariants = {
    hidden: { opacity: 0, y: isMobile ? 10 : 20 },
    visible: {
      opacity: 1,
      y: 0,
      transition: { duration: isMobile ? 0.3 : 0.5 }
    }
  };

  // Use simplified animations for reduced motion
  const finalSlideVariants = prefersReducedMotion ? accessibleSlideVariants : slideVariants;

  // Get background style for a slide
  const getSlideBackground = (slide) => {
    // Use solid color while loading as fallback
    if (isLoading) {
      return { backgroundColor: slide.background };
    }

    // Choose appropriate image based on device and loading state
    const placeholderImg = slide.placeholderImage;
    const fullImg = slide.backgroundImage;

    // Use placeholder for mobile or while full image is loading
    // Use full image when available
    const bgImageToUse = 
      (isMobile || !loadedImages[fullImg] || imageLoadFallback) 
        ? placeholderImg 
        : fullImg;

    const isImageLoaded = loadedImages[bgImageToUse];

    return {
      backgroundColor: slide.background,
      backgroundImage: isImageLoaded ? `url(${bgImageToUse})` : 'none',
      backgroundSize: 'cover',
      backgroundPosition: 'center'
    };
  };

  return (
    <section 
      className="hero-slider-section" 
      ref={sliderRef}
      onTouchStart={handleTouchStart}
      onTouchMove={handleTouchMove}
      onTouchEnd={handleTouchEnd}
    >
      <div className="hero-slider">
        <AnimatePresence initial={false}>
          {slides.map((slide, index) => (
            activeSlide === index && (
              <motion.div
                key={`slide-${index}`}
                className="hero-slide"
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                exit={{ opacity: 0 }}
                transition={{ duration: prefersReducedMotion ? 0 : 0.8 }}
                style={getSlideBackground(slide)}
              >
                <div className="slide-overlay"></div>
                <div className="slide-content">
                  <div className="slide-content-inner">
                    {slide.secondaryText && (
                      <AnimatedElement>
                        <div className="slide-secondary-text">
                          {slide.secondaryText}
                        </div>
                      </AnimatedElement>
                    )}

                    <AnimatedElement delay={0.1}>
                      <h1 className="slide-title">{slide.title}</h1>
                    </AnimatedElement>

                    {slide.date && (
                      <AnimatedElement delay={0.2}>
                        <div className="slide-date">{slide.date}</div>
                      </AnimatedElement>
                    )}

                    <AnimatedElement delay={0.3}>
                      <p className="slide-subtitle">{slide.subtitle}</p>
                    </AnimatedElement>

                    <AnimatedElement delay={0.4}>
                      <a href={slide.buttonLink} className="slide-button">
                        <span className="button-text">{slide.button}</span>
                        <span className="button-arrow">
                          <svg width="16" height="16" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                            <path d="M5 12H19" stroke="currentColor" strokeWidth="1.5" strokeLinecap="round" strokeLinejoin="round"/>
                            <path d="M12 5L19 12L12 19" stroke="currentColor" strokeWidth="1.5" strokeLinecap="round" strokeLinejoin="round"/>
                          </svg>
                        </span>
                      </a>
                    </AnimatedElement>
                  </div>
                </div>
              </motion.div>
            )
          ))}
        </AnimatePresence>
      </div>

      <div className="slider-controls">
        <div className="slider-dots">
          {slides.map((_, index) => (
            <button
              key={`dot-${index}`}
              className={`slider-dot ${activeSlide === index ? 'active' : ''}`}
              onClick={() => handleIndicatorClick(index)}
              aria-label={`Go to slide ${index + 1}`}
            >
              <span className="dot-progress"></span>
            </button>
          ))}
        </div>

        <button 
          className="slider-control-button pause-button"
          onClick={() => setIsPaused(!isPaused)}
          aria-label={isPaused ? "Play slideshow" : "Pause slideshow"}
        >
          {isPaused ? (
            <svg width="20" height="20" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
              <path d="M6 4L18 12L6 20V4Z" stroke="currentColor" strokeWidth="1.5" strokeLinecap="round" strokeLinejoin="round"/>
            </svg>
          ) : (
            <svg width="20" height="20" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
              <path d="M10 4H6V20H10V4Z" stroke="currentColor" strokeWidth="1.5" strokeLinecap="round" strokeLinejoin="round"/>
              <path d="M18 4H14V20H18V4Z" stroke="currentColor" strokeWidth="1.5" strokeLinecap="round" strokeLinejoin="round"/>
            </svg>
          )}
        </button>
      </div>
    </section>
  );
};

export default HeroSlider;
