import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router } from 'react-router-dom';
import './styles/App.css';
import Header from './components/Header';

// Import all your sections here
import HeroSlider from './components/sections/HeroSlider';
import FeaturedItems from './components/sections/FeaturedItems';
import PrivateDining from './components/sections/PrivateDining';
import Celebrations from './components/sections/Celebrations';
import OurStory from './components/sections/OurStory';
import ButtonDemo from './components/ButtonDemo';
import Footer from './components/layout/Footer';

function App() {
  const [visibleSections, setVisibleSections] = useState([]);

  // Function to handle section visibility on scroll
  useEffect(() => {
    const handleScroll = () => {
      const sections = document.querySelectorAll('section');
      const scrollPosition = window.scrollY + window.innerHeight * 0.5;

      sections.forEach(section => {
        const sectionTop = section.offsetTop;
        const sectionHeight = section.offsetHeight;

        if (scrollPosition >= sectionTop && scrollPosition < sectionTop + sectionHeight) {
          setVisibleSections(prevVisibleSections => {
            if (!prevVisibleSections.includes(section.id) && section.id) {
              return [...prevVisibleSections, section.id];
            }
            return prevVisibleSections;
          });
        }
      });
    };

    window.addEventListener('scroll', handleScroll);
    handleScroll(); // Call once on mount to check initial visible sections

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <Router>
      <div className="App">
        <Header />

        <main className="main-content">
          <HeroSlider />

          <section id="featured-items" className={`${visibleSections.includes('featured-items') ? 'visible' : ''}`}>
            <FeaturedItems />
          </section>

          <section id="private-dining" className={`${visibleSections.includes('private-dining') ? 'visible' : ''}`}>
            <PrivateDining />
          </section>

          <section id="celebrations" className={`${visibleSections.includes('celebrations') ? 'visible' : ''}`}>
            <Celebrations />
          </section>

          <section id="our-story" className={`${visibleSections.includes('our-story') ? 'visible' : ''}`}>
            <OurStory />
          </section>

          <section id="button-demo" className={`${visibleSections.includes('button-demo') ? 'visible' : ''}`}>
            <ButtonDemo />
          </section>
        </main>

        <Footer />
      </div>
    </Router>
  );
}

export default App;
