import React, { useState, useEffect, useRef } from 'react';
import { Link as RouterLink } from 'react-router-dom';
import './Header.css';

const Header = () => {
  // State for sticky header
  const [isSticky, setIsSticky] = useState(false);
  
  // State for active dropdowns
  const [activeDropdown, setActiveDropdown] = useState(null);
  
  // Refs for detecting clicks outside dropdowns
  const dropdownRefs = useRef([]);
  
  // Restaurant locations array
  const restaurants = [
    {
      name: 'Galvin La Chapelle',
      address: '35 Spital Square, London, E1 6DY',
      link: '/restaurants/la-chapelle'
    },
    {
      name: 'Galvin Bistrot & Bar',
      address: '35 Spital Square, London, E1 6DY',
      link: '/restaurants/bistrot-bar'
    },
    {
      name: 'Galvin Bar & Grill',
      address: 'Kimpton Fitzroy, Bloomsbury, WC1B 5BE',
      link: '/restaurants/bar-grill'
    },
    {
      name: 'Galvin Green Man',
      address: 'Main Road, Howe Street, Essex, CM3 1BG',
      link: '/restaurants/green-man'
    }
  ];
  
  // Menu items array
  const menuItems = [
    {
      label: 'MENUS',
      link: '/menus',
      hasDropdown: true,
      dropdownItems: [
        { label: 'À La Carte', link: '/menus/a-la-carte' },
        { label: 'Tasting Menu', link: '/menus/tasting-menu' },
        { label: 'Wine List', link: '/menus/wine-list' },
        { label: 'Sunday Lunch', link: '/menus/sunday-lunch' }
      ]
    },
    {
      label: 'PRIVATE DINING',
      link: '/private-dining',
      hasDropdown: true,
      dropdownItems: [
        { label: 'Club Room', link: '/private-dining/club-room' },
        { label: 'Gallery', link: '/private-dining/gallery' },
        { label: 'The Crypt', link: '/private-dining/crypt' },
      ]
    },
    {
      label: 'GIFT VOUCHERS',
      link: '/gift-vouchers',
      hasDropdown: false
    },
    {
      label: 'WEDDINGS',
      link: '/weddings',
      hasDropdown: false
    },
    {
      label: 'OUR STORY',
      link: '/our-story',
      hasDropdown: false
    },
    {
      label: 'CONTACT',
      link: '/contact',
      hasDropdown: false
    }
  ];
  
  // Add scroll event listener for sticky header
  useEffect(() => {
    const handleScroll = () => {
      setIsSticky(window.pageYOffset > 80);
    };
    
    window.addEventListener('scroll', handleScroll);
    
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);
  
  // Add event listener for clicks outside dropdown
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (activeDropdown && !event.target.closest('.has-dropdown') && !event.target.closest('.locations-dropdown')) {
        setActiveDropdown(null);
      }
    };
    
    document.addEventListener('mousedown', handleClickOutside);
    
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [activeDropdown]);
  
  // Toggle dropdown function
  const toggleDropdown = (index) => {
    setActiveDropdown(activeDropdown === index ? null : index);
  };
  
  return (
    <header className="site-header">
      {/* Top Bar - Dark Background */}
      <div className={`top-bar ${isSticky ? 'sticky' : ''}`}>
        <div className="container">
          <div className="top-bar-inner">
            {/* Top Bar Left - Locations */}
            <div className="top-bar-left">
              <div className="locations-dropdown">
                <button className="locations-btn">
                  <div className="hamburger-icon">
                    <span className="line"></span>
                    <span className="line"></span>
                    <span className="line"></span>
                  </div>
                  <span className="locations-text">LOCATIONS</span>
                </button>
                <div className="locations-dropdown-content">
                  <div className="locations-header">
                    <span>OUR RESTAURANTS</span>
                  </div>
                  <div className="locations-list">
                    {restaurants.map((restaurant, index) => (
                      <RouterLink to={restaurant.link} className="location-item" key={index}>
                        <div className="location-name">{restaurant.name}</div>
                        <div className="location-address">{restaurant.address}</div>
                      </RouterLink>
                    ))}
                  </div>
                </div>
              </div>
            </div>
            
            {/* Top Bar Center - Logo */}
            <div className="top-bar-center">
              <RouterLink to="/" className="logo-link">
                <span className="logo-text">KABOUL GOURMET</span>
              </RouterLink>
            </div>
            
            {/* Top Bar Right - Contact & Book */}
            <div className="top-bar-right">
              <a href="tel:+442073751843" className="phone-link">020 7375 1843</a>
              <RouterLink to="/gift-vouchers" className="gift-link">GIFT VOUCHERS</RouterLink>
              <div className="book-dropdown">
                <RouterLink to="/book" className="book-btn">BOOK</RouterLink>
                <div className="book-dropdown-content">
                  <RouterLink to="/book/lunch">LUNCH</RouterLink>
                  <RouterLink to="/book/dinner">DINNER</RouterLink>
                  <RouterLink to="/book/private-dining">PRIVATE DINING</RouterLink>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
      
      {/* Bottom Bar - White Background */}
      <div className={`bottom-bar ${isSticky ? 'sticky' : ''}`}>
        <div className="container">
          <div className="bottom-bar-inner">
            {/* Restaurant Name & Location */}
            <div className="restaurant-info">
              <RouterLink to="/" className="restaurant-name">KABOUL GOURMET</RouterLink>
              <span className="restaurant-location">SPITALFIELDS, LONDON</span>
            </div>
            
            {/* Main Navigation */}
            <nav className="main-menu">
              <ul className="menu-list">
                {menuItems.map((item, index) => (
                  <li 
                    key={index} 
                    className={item.hasDropdown ? 'has-dropdown' : ''}
                    ref={el => dropdownRefs.current[index] = el}
                  >
                    {item.hasDropdown ? (
                      <>
                        <a 
                          href="#!" 
                          onClick={(e) => {
                            e.preventDefault();
                            toggleDropdown(index);
                          }}
                          className={activeDropdown === index ? 'active' : ''}
                        >
                          {item.label}
                        </a>
                        <div className={`dropdown-menu ${activeDropdown === index ? 'active' : ''}`}>
                          {item.dropdownItems.map((dropdownItem, i) => (
                            <RouterLink to={dropdownItem.link} key={i}>
                              {dropdownItem.label}
                            </RouterLink>
                          ))}
                        </div>
                      </>
                    ) : (
                      <RouterLink to={item.link}>{item.label}</RouterLink>
                    )}
                  </li>
                ))}
              </ul>
            </nav>
          </div>
        </div>
      </div>
    </header>
  );
};

export default Header;
