import React, { useState, useEffect, useRef } from 'react';
import './Header.css';

const Header = () => {
  // State for sticky header
  const [isSticky, setIsSticky] = useState(false);
  
  // State for active dropdowns
  const [activeDropdown, setActiveDropdown] = useState(null);
  
  // State for mobile menu
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);
  
  // Refs for detecting clicks outside dropdowns
  const dropdownRefs = useRef([]);
  
  // Restaurant locations array
  const restaurants = [
    {
      name: 'Galvin La Chapelle',
      address: '35 Spital Square, London, E1 6DY',
      link: '/restaurants/la-chapelle'
    },
    {
      name: 'Galvin Bistrot & Bar',
      address: '35 Spital Square, London, E1 6DY',
      link: '/restaurants/bistrot-bar'
    },
    {
      name: 'Galvin Bar & Grill',
      address: 'Kimpton Fitzroy, Bloomsbury, WC1B 5BE',
      link: '/restaurants/bar-grill'
    },
    {
      name: 'Galvin Green Man',
      address: 'Main Road, Howe Street, Essex, CM3 1BG',
      link: '/restaurants/green-man'
    }
  ];
  
  // Menu items array
  const menuItems = [
    {
      label: 'MENUS',
      link: '/menus',
      hasDropdown: true,
      dropdownItems: [
        { label: 'À La Carte', link: '/menus/a-la-carte' },
        { label: 'Tasting Menu', link: '/menus/tasting-menu' },
        { label: 'Wine List', link: '/menus/wine-list' },
        { label: 'Sunday Lunch', link: '/menus/sunday-lunch' }
      ]
    },
    {
      label: 'WHAT\'S ON',
      link: '/whats-on',
      hasDropdown: false
    },
    {
      label: 'PRIVATE DINING',
      link: '/private-dining',
      hasDropdown: true,
      dropdownItems: [
        { label: 'Club Room', link: '/private-dining/club-room' },
        { label: 'Gallery', link: '/private-dining/gallery' },
        { label: 'The Crypt', link: '/private-dining/crypt' },
      ]
    },
    {
      label: 'ABOUT',
      link: '/about',
      hasDropdown: false
    },
    {
      label: 'REVIEWS',
      link: '/reviews',
      hasDropdown: false
    },
    {
      label: 'CONTACT',
      link: '/contact',
      hasDropdown: false
    },
    {
      label: 'NEWSLETTER SIGNUP',
      link: '/newsletter',
      hasDropdown: false
    }
  ];
  
  // Add scroll event listener for sticky header
  useEffect(() => {
    const handleScroll = () => {
      setIsSticky(window.pageYOffset > 80);
    };
    
    window.addEventListener('scroll', handleScroll);
    
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);
  
  // Add event listener for clicks outside dropdown
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (activeDropdown !== null && !dropdownRefs.current[activeDropdown]?.contains(event.target)) {
        setActiveDropdown(null);
      }
    };
    
    document.addEventListener('mousedown', handleClickOutside);
    
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [activeDropdown]);
  
  // Toggle dropdown function
  const toggleDropdown = (index) => {
    setActiveDropdown(activeDropdown === index ? null : index);
  };

  // Toggle mobile menu
  const toggleMobileMenu = () => {
    setMobileMenuOpen(!mobileMenuOpen);
  };

  // Handle navigation without React Router
  const handleNavigation = (e, path) => {
    e.preventDefault();
    console.log(`Navigation to ${path} would happen here`);
    // In a real app with React Router, you would use history.push(path)
  };
  
  return (
    <header className="site-header">
      {/* Top Bar - Dark Background */}
      <div className={`top-bar ${isSticky ? 'sticky' : ''}`}>
        <div className="container">
          <div className="top-bar-content">
            {/* Left - Locations */}
            <div className="top-bar-left">
              <button className="locations-btn">
                <span className="hamburger-icon">
                  <span className="line"></span>
                  <span className="line"></span>
                  <span className="line"></span>
                </span>
                <span className="locations-text">LOCATIONS</span>
              </button>
            </div>
            
            {/* Center - Main Logo */}
            <div className="top-bar-center">
              <a href="/" className="main-logo-link" onClick={(e) => handleNavigation(e, "/")}>
                <span className="main-logo-text">GALVIN</span>
              </a>
            </div>
            
            {/* Right - Contact, Gifts, Book Now */}
            <div className="top-bar-right">
              <a href="tel:02072990400" className="phone-link">020 7299 0400</a>
              <a href="/gifts" className="gifts-link" onClick={(e) => handleNavigation(e, "/gifts")}>
                GIFTS
              </a>
              <a href="/book-now" className="book-now-btn" onClick={(e) => handleNavigation(e, "/book-now")}>
                <span className="calendar-icon"></span>
                BOOK NOW
              </a>
              
              {/* Mobile Menu Toggle (only visible on mobile) */}
              <div className="mobile-menu-toggle" onClick={toggleMobileMenu}>
                <div className={`hamburger ${mobileMenuOpen ? 'active' : ''}`}>
                  <span className="bar"></span>
                  <span className="bar"></span>
                  <span className="bar"></span>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
      
      {/* Bottom Bar - Light Background */}
      <div className={`bottom-bar ${isSticky ? 'sticky' : ''}`}>
        <div className="container">
          <div className="bottom-bar-content">
            {/* Restaurant Name */}
            <div className="restaurant-name">
              <a href="/" onClick={(e) => handleNavigation(e, "/")}>
                Galvin La Chapelle
              </a>
            </div>
            
            {/* Main Navigation */}
            <nav className="main-navigation">
              <ul className="nav-menu">
                {menuItems.map((item, index) => (
                  <li 
                    key={index} 
                    className={`nav-item ${item.hasDropdown ? 'has-dropdown' : ''}`}
                    ref={el => dropdownRefs.current[index] = el}
                  >
                    {item.hasDropdown ? (
                      <>
                        <a 
                          href={item.link} 
                          onClick={(e) => {
                            e.preventDefault();
                            toggleDropdown(index);
                          }}
                          className={activeDropdown === index ? 'active' : ''}
                        >
                          {item.label}
                        </a>
                        <div className={`dropdown-content ${activeDropdown === index ? 'active' : ''}`}>
                          <ul>
                            {item.dropdownItems.map((dropdownItem, i) => (
                              <li key={i}>
                                <a 
                                  href={dropdownItem.link} 
                                  onClick={(e) => handleNavigation(e, dropdownItem.link)}
                                >
                                  {dropdownItem.label}
                                </a>
                              </li>
                            ))}
                          </ul>
                        </div>
                      </>
                    ) : (
                      <a 
                        href={item.link}
                        onClick={(e) => handleNavigation(e, item.link)}
                      >
                        {item.label}
                      </a>
                    )}
                  </li>
                ))}
              </ul>
            </nav>
          </div>
        </div>
      </div>
      
      {/* Mobile Menu */}
      <div className={`mobile-menu ${mobileMenuOpen ? 'active' : ''}`}>
        <div className="container">
          <ul className="mobile-nav-menu">
            {menuItems.map((item, index) => (
              <li key={index} className="mobile-nav-item">
                <a 
                  href={item.link}
                  onClick={(e) => handleNavigation(e, item.link)}
                >
                  {item.label}
                </a>
                
                {item.hasDropdown && (
                  <ul className="mobile-dropdown">
                    {item.dropdownItems.map((dropdownItem, i) => (
                      <li key={i} className="mobile-dropdown-item">
                        <a 
                          href={dropdownItem.link} 
                          onClick={(e) => handleNavigation(e, dropdownItem.link)}
                        >
                          {dropdownItem.label}
                        </a>
                      </li>
                    ))}
                  </ul>
                )}
              </li>
            ))}
          </ul>
        </div>
      </div>
    </header>
  );
};

export default Header;
