import React from 'react';
import './styles/App.css';

// Layout Components
import SideMenu from './components/layout/SideMenu';
import TopNav from './components/layout/TopNav';
import Footer from './components/layout/Footer';

// Section Components
import HeroBanner from './components/sections/HeroBanner';
// import Features from './components/sections/Features';
// import Description from './components/sections/Description';
// import Testimonials from './components/sections/Testimonials';
// import Seasonal from './components/sections/Seasonal';
// import Celebrations from './components/sections/Celebrations';
// import PrivateDining from './components/sections/PrivateDining';
// import OurStory from './components/sections/OurStory';
// import Contact from './components/sections/Contact';
// import Address from './components/sections/Address';
// import FAQ from './components/sections/FAQ';

function App() {
  // State for side menu
  const [sideMenuActive, setSideMenuActive] = React.useState(false);

  // Toggle side menu
  const toggleSideMenu = () => {
    setSideMenuActive(!sideMenuActive);
  };

  return (
    <div className="App">
      <SideMenu isActive={sideMenuActive} onClose={() => setSideMenuActive(false)} />
      
      <div 
        className={`menu-overlay ${sideMenuActive ? 'active' : ''}`} 
        onClick={() => setSideMenuActive(false)}
      />

      <TopNav onOpenMenu={toggleSideMenu} />
      
      <main>
        <HeroBanner />
        {/* 
        <Features />
        <Description />
        <Testimonials />
        <Seasonal />
        <Celebrations />
        <PrivateDining />
        <OurStory />
        <Contact />
        <Address />
        <FAQ />
        */}
      </main>
      
      <Footer />
      
      <div className="restaurant-description">
        Michelin-quality Afghan restaurant Kaboul Gourmet in Chelsea, London, featuring a traditional yet modern Afghan menu with authentic flavors.
      </div>
    </div>
  );
}

export default App;
