import React from 'react';
import { motion } from 'framer-motion';

/**
 * AnimatedElement Component
 * 
 * A wrapper component that adds hover animations to any element
 * 
 * @param {string} as - The element type to render (default: 'div')
 * @param {string} className - Additional CSS classes
 * @param {string} hoverEffect - Type of hover effect: 'scale', 'float', 'glow', or 'none'
 * @param {node} children - Element content
 */
const AnimatedElement = ({ 
  as = 'div', 
  className = '', 
  hoverEffect = 'scale', 
  children,
  ...props 
}) => {
  // Define hover animations
  const hoverAnimations = {
    scale: {
      scale: 1.05,
      transition: { duration: 0.2, ease: "easeOut" }
    },
    float: {
      y: -5,
      transition: { duration: 0.3, ease: "easeOut" }
    },
    glow: {
      boxShadow: "0px 0px 15px rgba(241, 180, 56, 0.4)",
      transition: { duration: 0.3, ease: "easeOut" }
    },
    none: {}
  };

  // Get the animation for the specified effect
  const hoverAnimation = hoverAnimations[hoverEffect] || hoverAnimations.none;
  
  // Create the motion component with the specified element type
  const MotionComponent = motion[as];
  
  return (
    <MotionComponent
      className={`animated-element ${className}`}
      whileHover={hoverAnimation}
      {...props}
    >
      {children}
    </MotionComponent>
  );
};

export default AnimatedElement;
