import React, { useState } from 'react';
import './SeasonalFresh.css';

const SeasonalFresh = () => {
  const [activeTestimonial, setActiveTestimonial] = useState(0);

  const testimonials = [
    {
      quote: "The Sunday lunch complete with children's menu – is excellent value.",
      author: "Great British Chefs"
    },
    {
      quote: "Buzzing with excitement and glamour, with a mixed crowd of artists and media characters.",
      author: "Great British Chefs"
    },
    {
      quote: "The best Afghan cuisine in the heart of London.",
      author: "Hot Dinners"
    }
  ];

  const handleIndicatorClick = (index) => {
    setActiveTestimonial(index);
  };

  return (
    <section id="seasonal" className="seasonal-section">
      <div className="seasonal-grid">
        <div className="seasonal-image">
          <div className="food-presentation">
            <div className="plate-circle"></div>
            <div className="food-item food-main"></div>
            <div className="food-item food-garnish"></div>
            <div className="food-item food-sauce"></div>
            <div className="pouring-sauce"></div>
          </div>
        </div>

        <div className="seasonal-content">
          <div className="content-wrapper">
            <h2 className="section-title">SEASONAL FRESH</h2>
            <div className="divider"></div>

            <h3 className="section-subtitle">Only the very best ingredients</h3>

            <p className="seasonal-description">
              Our menus at Kaboul Gourmet celebrate only the best in produce of the season. With frequently changing menus our Head Chef continues to adapt our offering to ensure we only serve our guests the very best.
            </p>

            <a href="#menus" className="seasonal-button">MENUS</a>

            <div className="testimonial-container">
              <div className="quote-icon">&ldquo;</div>
              <div className="testimonial-content">
                {testimonials.map((testimonial, index) => (
                  <div key={index} className={`testimonial ${index === activeTestimonial ? 'active' : ''}`}>
                    <p>{testimonial.quote}</p>
                    <cite>— {testimonial.author}</cite>
                  </div>
                ))}
              </div>

              <div className="testimonial-indicators">
                {testimonials.map((_, index) => (
                  <button 
                    key={index} 
                    className={`indicator ${index === activeTestimonial ? 'active' : ''}`}
                    onClick={() => handleIndicatorClick(index)}
                    aria-label={`Testimonial ${index + 1}`}
                  />
                ))}
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default SeasonalFresh;
