import React from 'react';
import './HeroBanner.css';
import backgroundImage from '../../images/hero-background.jpg';

const HeroBanner = () => {
  return (
    <section className="hero-banner" style={{ backgroundImage: `url(${backgroundImage})` }}>
      <div className="hero-content">
        <h1>AUTHENTIC AFGHAN CUISINE</h1>
        <div className="divider"></div>
        <p>A modern Afghan dining experience in the heart of London</p>
        <p className="subtitle">Bringing the rich flavors of Afghanistan to Chelsea</p>
        <div className="hero-buttons">
          <a href="#book" className="btn-primary">BOOK A TABLE</a>
          <a href="#menus" className="btn-secondary">VIEW MENUS</a>
        </div>
      </div>
      <div className="hero-overlay"></div>
    </section>
  );
};

export default HeroBanner;
